import styled from 'styled-components'
import { useState } from 'react'
import { Box } from './styled'
import { Input } from './styled'



const Button = styled.button`
  border-radius: 3px;
  border: 2px solid palevioletred;
  color: palevioletred;
  margin: 0 1em;
  padding: 0.25em 1em;
`



export const MainPanel = () => {
    let [formKey, setFormKey] = useState("");
    let [formSign, setFormSign] = useState("");
    let [formMessage, setFormMessage] = useState("");

    let [response, setResponse] = useState("");


    const onSubmit = () => {
        fetch(`http://localhost:8080/verifySign?key=${encodeURIComponent(formKey)}&sign=${encodeURIComponent(formSign)}&message=${encodeURIComponent(formMessage)}`)
        .then(data => data.json())
        .then(data => setResponse(data))
        .catch(error => console.log(error))
    }

    return(
        <Box>
            Hello, world! 

            key:
            <Input style={{borderRadius: 5, borderWidth: 0}} value={formKey} onChange={(e) => setFormKey(e.target.value)}></Input>

            sign:
            <Input style={{borderRadius: 5}} value={formSign} onChange={(e) => setFormSign(e.target.value)}></Input>

            message:
            <Input style={{borderRadius: 5}} value={formMessage} onChange={(e) => setFormMessage(e.target.value)}></Input>

            <Button onClick={onSubmit}>check</Button>
            <br/>
            {JSON.stringify(response)}
        </Box>
    )
}
