import styled from 'styled-components'
import { useState } from 'react'
import { Box } from './styled'
import { Input } from './styled'

const Button = styled.button`
  border-radius: 3px;
  border: 2px solid palevioletred;
  color: palevioletred;
  margin: 0 1em;
  padding: 0.25em 1em;
`



export const IdentityPanel = () => {

    let [response, setResponse] = useState("");


    const onSubmit = () => {
        fetch(`http://localhost:8080/getIdentity`)
        .then(data => data.json())
        .then(data => setResponse(data))
        .catch(error => console.log(error))
    }

    return(
        <Box>
            <b>Step 1: </b> time to get your identity!
            private:
            <textarea style={{maxWidth: 300, wordBreak: "break-all"}} value={response.privateKey}>
            </textarea>

            public:
            <textarea style={{maxWidth: 300, wordBreak: "break-all"}} value={response.publicKey}>
            </textarea>

            <Button onClick={onSubmit}>Get</Button>

        </Box>
    )
}
