import styled from 'styled-components'
import { useContext, useState } from 'react'
import { Box } from './styled'
import { TextArea } from './styled'
import { useDispatch, useSelector } from 'react-redux'
import { setKeys } from '../redux/appActions'

const Button = styled.button`
  border-radius: 10px;
  border: 0px;
  margin: 0 1em;
  padding: 0.25em 1em;
`



export const IdentityPanel = (props) => {

    let [response, setResponse] = useState("");

    let dispatch = useDispatch();
    let appState = useSelector(state => state.app);

    const onSubmit = () => {
        fetch(`http://localhost:8080/getIdentity`)
        .then(data => data.json())
        .then(data => {
            dispatch(setKeys({privateKey: response.privateKey, publicKey: response.publicKey}));
        })
        .catch(error => console.log(error))
    }

    return(
        <Box>
            <p><b>Step 1: </b> get your identity</p> 
            <TextArea style={{maxWidth: 300, wordBreak: "break-all"}} placeholder="private key" value={appState.privateKey}>
            </TextArea>

            <TextArea style={{maxWidth: 300, wordBreak: "break-all"}}  placeholder="public key" value={appState.publicKey}>
            </TextArea>

            <Button onClick={onSubmit}>Get</Button>

        </Box>
    )
}
