import logo from './logo.svg';
import './App.css';
import { MainPanel } from './components/MainPanel';
import styled from 'styled-components';
import { IdentityPanel } from './components/IdentityPanel';
import { SignPanel } from './components/SignPanel';
import { useState } from 'react';
import { createContext } from 'react';

const Box = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
`


function App() {

  const Context = createContext({
    privateKey: "",
    setPrivateKey: () => {},
    publicKey: "",
    setPublicKey: () => {},
    sign: "",
    setSign: () => {}
  });

  const [privateKey, setPrivateKey] = useState("");
  const [publicKey, setPublicKey] = useState("");
  const [sign, setSign] = useState("");

  const value = {privateKey, setPrivateKey, publicKey, setPublicKey, sign, setSign};

  return (
    <Context.Provider value={value}>
      <Box>
        <IdentityPanel setPrivateKey={setPrivateKey} setPublicKey={setPublicKey} />
        <SignPanel privateKey={privateKey} setSign={setSign} />
        <MainPanel publicKey={publicKey} sign={sign}/>
      </Box>
    </Context.Provider>
  );
}

export default App;
