import styled from 'styled-components'
import { useContext, useState } from 'react'
import { Box } from './styled'
import { TextArea } from './styled'

const Button = styled.button`
  border-radius: 10px;
  border: 0px;
  margin: 0 1em;
  padding: 0.25em 1em;
`



export const IdentityPanel = (props) => {

    let [response, setResponse] = useState("");

    let {privateKey, setPrivateKey, publicKey, setPublicKey} = useContext(Context);

    const onSubmit = () => {
        fetch(`http://localhost:8080/getIdentity`)
        .then(data => data.json())
        .then(data => {
            setResponse(data);
            props.setPrivateKey(response.privateKey)
        })
        .catch(error => console.log(error))
    }

    return(
        <Box>
            <p><b>Step 1: </b> get your identity</p> 
            <TextArea style={{maxWidth: 300, wordBreak: "break-all"}} placeholder="private key" value={response.privateKey}>
            </TextArea>

            <TextArea style={{maxWidth: 300, wordBreak: "break-all"}}  placeholder="public key" value={response.publicKey}>
            </TextArea>

            <Button onClick={onSubmit}>Get</Button>

        </Box>
    )
}
