import styled from 'styled-components'
import { useEffect, useState } from 'react'
import { Box } from './styled'
import { Input } from './styled'
import { useDispatch, useSelector } from 'react-redux'
import { setSign } from '../redux/appActions'




const Button = styled.button`
  border-radius: 3px;
  border: 2px solid palevioletred;
  color: palevioletred;
  margin: 0 1em;
  padding: 0.25em 1em;
`


export const SignPanel = () => {
    const [formMessage, setFormMessage] = useState("");
    const [formKey, setFormKey] = useState("");

    const appState = useSelector(state => state.app);
    const dispatch = useDispatch();

    useEffect(() => {
        setFormKey(appState.privateKey)
    }, [appState.privateKey])


    const onSubmit = () => {
        fetch(`http://localhost:8080/signMessage?key=${encodeURIComponent(formKey)}&message=${encodeURIComponent(formMessage)}`)
        .then(data => data.json())
        .then(data => dispatch(setSign(data.sign)))
        .catch(error => console.log(error))
    }

    return(
        <Box>
            <p><b>Step 2:</b> sign.</p>

            (key)
            <Input value={formKey} onChange={(e) => setFormKey(e.target.value)}></Input>

            (message)
            <Input value={formMessage} onChange={(e) => setFormMessage(e.target.value)}></Input>

            <Button onClick={onSubmit}>check</Button>
            <br/>

            <textarea style={{maxWidth: 300, wordBreak: "break-all"}} value={appState.sign}>
            </textarea>

        </Box>
    )
}
