import styled from 'styled-components'
import { useState } from 'react'


const Button = styled.button`
  border-radius: 3px;
  border: 2px solid palevioletred;
  color: palevioletred;
  margin: 0 1em;
  padding: 0.25em 1em;
`

const Box = styled.div`
    border: solid;
    border-weight: 2px;
    border-radius: 10px;
    background-color: #272727;
    border-color: #656565;

    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

    padding: 50px;
`


export const MainPanel = () => {
    let [formKey, setFormKey] = useState("");
    let [formSign, setFormSign] = useState("");
    let [formMessage, setFormMessage] = useState("");

    let [response, setResponse] = useState("");


    const onSubmit = () => {
        fetch(`http://localhost:8080/verifySign?key=${encodeURIComponent(formKey)}&sign=${encodeURIComponent(formSign)}&message=${encodeURIComponent(formMessage)}`)
        .then(data => data.json())
        .then(data => setResponse(data))
        .catch(error => console.log(error))
    }

    return(
        <Box>
            Hello, world! 

            key:
            <input value={formKey} onChange={(e) => setFormKey(e.target.value)}></input>

            sign:
            <input value={formSign} onChange={(e) => setFormSign(e.target.value)}></input>

            message:
            <input value={formMessage} onChange={(e) => setFormMessage(e.target.value)}></input>

            <Button onClick={onSubmit}>check</Button>
            <br/>
            {JSON.stringify(response)}
        </Box>
    )
}
