import styled from 'styled-components'
import { useEffect, useState } from 'react'
import { Box } from './styled'
import { Input } from './styled'
import { useSelector } from 'react-redux'

const Button = styled.button`
  border-radius: 10px;
  border: 2px solid palevioletred;
  color: palevioletred;
  margin: 0 1em;
  padding: 0.25em 1em;
`

export const MainPanel = () => {
    const [formKey, setFormKey] = useState("");
    const [formSign, setFormSign] = useState("");
    const [formMessage, setFormMessage] = useState("");
    const [response, setResponse] = useState("");
    const appState = useSelector(state => state.app);

    useEffect(() => {
        setFormSign(appState.sign);
        setFormKey(appState.publicKey);

    }, [appState]);

    const onSubmit = () => {
        fetch(`http://localhost:8080/verifySign?key=${encodeURIComponent(formKey)}&sign=${encodeURIComponent(formSign)}&message=${encodeURIComponent(formMessage)}`)
        .then(data => data.json())
        .then(data => setResponse(data))
        .catch(error => console.log(error))
    }

    return(
        <Box>
            <p><b>Step 3: </b> verify.</p>

            key:
            <Input  value={formKey} onChange={(e) => setFormKey(e.target.value)}></Input>

            sign:
            <Input  value={formSign} onChange={(e) => setFormSign(e.target.value)}></Input>

            message:
            <Input value={formMessage} onChange={(e) => setFormMessage(e.target.value)}></Input>

            <Button onClick={onSubmit}>check</Button>
            <br/>
            {JSON.stringify(response)}
        </Box>
    )
}
