import styled from 'styled-components'
import { useState } from 'react'


const Button = styled.button`
  border-radius: 3px;
  border: 2px solid palevioletred;
  color: palevioletred;
  margin: 0 1em;
  padding: 0.25em 1em;
`

const Box = styled.div`
    border: solid;
    border-weight: 2px;
    border-radius: 10px;
    background-color: #272727;

    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

    padding: 50px;
`


export const SignPanel = () => {
    let [formKey, setFormKey] = useState("");
    let [formMessage, setFormMessage] = useState("");

    let [response, setResponse] = useState("");


    const onSubmit = () => {
        fetch(`http://localhost:8080/signMessage?key=${encodeURIComponent(formKey)}&message=${encodeURIComponent(formMessage)}`)
        .then(data => data.json())
        .then(data => setResponse(data))
        .catch(error => console.log(error))
    }

    return(
        <Box>
            Hello, world Let's sign! 

            key:
            <input value={formKey} onChange={(e) => setFormKey(e.target.value)}></input>

            message:
            <input value={formMessage} onChange={(e) => setFormMessage(e.target.value)}></input>

            <Button onClick={onSubmit}>check</Button>
            <br/>

            <textarea style={{maxWidth: 300, wordBreak: "break-all"}} value={response.sign}>
            </textarea>

        </Box>
    )
}
