import styled from 'styled-components'
import { useState } from 'react'
import { Box } from './styled'
import { Input } from './styled'




const Button = styled.button`
  border-radius: 3px;
  border: 2px solid palevioletred;
  color: palevioletred;
  margin: 0 1em;
  padding: 0.25em 1em;
`


export const SignPanel = () => {
    let [formKey, setFormKey] = useState("");
    let [formMessage, setFormMessage] = useState("");

    let [response, setResponse] = useState("");

    let appState = useSelector(state => state.app);


    const onSubmit = () => {
        fetch(`http://localhost:8080/signMessage?key=${encodeURIComponent(formKey)}&message=${encodeURIComponent(formMessage)}`)
        .then(data => data.json())
        .then(data => setResponse(data))
        .catch(error => console.log(error))
    }

    return(
        <Box>
            Hello, world Let's sign! 

            key:
            <Input value={appState.privateKey} onChange={(e) => setFormKey(e.target.value)}></Input>

            message:
            <Input value={formMessage} onChange={(e) => setFormMessage(e.target.value)}></Input>

            <Button onClick={onSubmit}>check</Button>
            <br/>

            <textarea style={{maxWidth: 300, wordBreak: "break-all"}} value={response.sign}>
            </textarea>

        </Box>
    )
}
